import { Component, EventEmitter, OnInit, Output, ViewChild } from "@angular/core";
import { NavigationEnd, Router } from "@angular/router";
import { SearchService } from "../service/search.service";

@Component({
  selector: "navbar",
  templateUrl: "./navbar.component.html",
  styleUrls: ["./navbar.component.css"],
})
export class NavbarComponent implements OnInit {

  constructor(

    private router: Router,

    private searchService:SearchService
  ) {}
  loginStatus: Boolean = false;

  public isMenuCollapsed = true;
  searchTerm = '';
  public username:any;
  @Output() onSearch: EventEmitter<any> = new EventEmitter();

  userList = [];

  capitalizaFirstAndLastName(fullName:string) {
    return fullName.toLowerCase().replace(/\b./g, function (a) {
      return a.toUpperCase();
    });
  }


  showSearch = true;
  ngOnInit(): void {
    this.router.events.subscribe(event => {
      if (event instanceof NavigationEnd) {
        this.showSearch = this.router.url === '/home';
      }
    });
  }


  search($event: any) {
    this.searchTerm = $event;
    this.searchService.updateSearch(this.searchTerm);
  }


}
<nav class="navbar navbar-expand-md p-3 mb-2  navbar-light" style="background-color: #515de8;">
    <a class="navbar-brand animate-charcter" href="#">Blogging Applicaion</a>

    <button class="navbar-toggler" type="button" data-toggle="collapse" data-target="#collapsibleNavbar">
        <span class="navbar-toggler-icon"></span>
    </button>

    <div class="collapse navbar-collapse" id="collapsibleNavbar">
                <a class="nav-link" routerLink="/home" (click)="isMenuCollapsed=true">Home</a>
                <a class="nav-link" routerLink="/teams" (click)="isMenuCollapsed=true">teams</a> 
    </div>
    <div *ngIf="showSearch">
            <input class="btn22" type="text" [(ngModel)]="searchTerm" (input)="search(searchTerm)" placeholder="Search...">
    </div>


</nav>
